(()=>{const e=new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting?e.target.classList.add("show"):e.target.classList.remove("show")}))}));document.querySelectorAll(".hidden").forEach((t=>e.observe(t)));const t=document.getElementById("leftSide"),o=e=>{const o=e.clientX/window.innerWidth*100;t.style.width=`${o}%`};document.onmousemove=e=>o(e),document.ontouchmove=e=>o(e.touches[0])})();